@Component({
  selector: 'app-day-jobs',
  templateUrl: './day-jobs.component.html',
  styleUrls: ['./day-jobs.component.css']
})
export class DayJobsComponent implements OnInit {

  locationIcon=faLocationDot;
  myJob=faCamera;
  fallback =
  'data:image/png;base64,';

  constructor( private router:Router, private productService:ProductService, private nzImageService: NzImageService) { }


  products:Product[]=[];
  ngOnInit(): void {
    

    this.productService.getProducts().subscribe((response)=>{
      this.products=response as Product[];
      console.log('products', this.products);
    
    });
   
  }



  //formatting the date
  formatDate(created_at: string): string {
    const timestamp = new Date(created_at);
    const now = new Date();
    const elapsed = now.getTime() - timestamp.getTime();
  
    if (elapsed < 60000) {
      return 'Just now';
    } else if (elapsed < 3600000) {
      const minutes = Math.floor(elapsed / 60000);
      return `${minutes} minute${minutes > 1 ? 's' : ''} ago`;
    } else if (elapsed < 86400000) {
      const hours = Math.floor(elapsed / 3600000);
      return `${hours} hour${hours > 1 ? 's' : ''} ago`;
    } else {
      const dateFormatter = new Intl.DateTimeFormat('en', { year: 'numeric', month: '2-digit', day: '2-digit' });
      const [{ value: day },,{ value: month },,{ value: year }] = dateFormatter.formatToParts(timestamp);
      return `${day}-${month}-${year}`;
    }
  }
  

 

  //view photos

  selectedProduct!:Product;
  onClick(id:number): void {
    
    this.productService.getProduct(id).subscribe((response) => {
      
      this.selectedProduct = response as Product;
      const images = this.selectedProduct.photos.map((photo: Photo) => ({
        src: photo.path,
        width: '200px',
        height: '200px',
        alt: 'Product Image'
      }));
  
      this.nzImageService.preview(images, { nzZoom: 1.5, nzRotate: 0 });
    });
  }

  
  

}
